### handle le plus court ###
def shortest_item(array)
    shortest = array[0] 
    shortest_length = shortest.length - 1
    array.each do |counter|
        counter_length = counter.length - 1
        if counter_length < shortest_length
            shortest = counter
            shortest_length = counter_length
        end
    end
    return shortest
end
shortest = shortest_item(handle_list)
puts "Nous avons #{shortest} identifié comme étant le handle le plus court de la liste."


### handle à 5 caractères court (@ ne compte pas) ###
def nb_xxxxx_items(array)
    nb_xxxxx = 0
    array.each do |counter|
        if counter.length == 6
            nb_xxxxx = nb_xxxxx + 1
        end   
    end
    return nb_xxxxx
end
nb_xxxxx = nb_xxxxx_items(handle_list)
puts "Nous avons #{nb_xxxxx} handle ne contenant que 5 caractères (le @ ne compte pas)."


### handle commençant par une majuscule (après @) ###
def nb_xUppercase_items(array)
    nb_xUppercase = 0
    array.each do |counter|
        counter = counter[1,1]
        if counter == counter.upcase
            nb_xUppercase = nb_xUppercase + 1
        end
    end
    return nb_xUppercase
end
nb_xUppercase = nb_xUppercase_items(handle_list)
puts "Nous avons #{nb_xUppercase} handle commençant par une majuscule (après @)."


